# Combien y a-t-il de handle dans cette array ?
def array_counter(journalists)
  puts
  total = journalists.count #fonction ruby pour compter le nbre de valeurs contenues dans un array
  puts "Il y a #{total} journalistes dans l'array."
  return total
end

# Quel est le handle le plus court de cette liste ?
def shortest_finder(journalists)
  puts
  shortest = journalists.sort_by(&:length)[0] #fonction ruby pour ordonner selon un critère à définir (&:...) et [0] pour afficher la valeur en 1ère position
  puts "Le handle le plus court est #{shortest}."
  return shortest
end

# Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
def fiver_counter(journalists)
  puts
  fivers = []
  journalists.each do |i|
    if i.length == (5+1) #fonction ruby pour obtenir le nombre de caractère d'une valeur
      fivers << i #création d'une array dans laquelle intégrer tous les handle avec 5 caractères
    end
  end
  fivers_number = fivers.count
  puts "Il y a #{fivers_number} handles de 5 caractères (#{fivers})." 
  return fivers_number
end

# Combien commencent par une majuscule (première lettre juste après le @) ?
def uppercase_counter(journalists)
  puts
  uppercasers_total = journalists.count {|e| e =~ /^@[[:upper:]]/} # https://stackoverflow.com/questions/52634598/how-to-count-words-in-array-that-start-with-a-capital-letter
  puts "Il y a #{uppercasers_total} handles qui commencent par une majuscule."
  return uppercasers_total
end

# Trie la liste de handle par ordre alphabétique.
def get_alphabetical_order(journalists)
  puts
  array_by_alphabetical_order = journalists.sort_by(&:downcase) #fonction ruby pour ordinner par ordre alphabétique
  puts "Voici la liste triée par ordre alphabétique:"
  print array_by_alphabetical_order
  return array_by_alphabetical_order 
end

# Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
def order_by_length(journalists)
  puts
  ordered_list_by_length = journalists.sort_by(&:length)
  puts "Voici la liste triée par longueur des handles:"
  print ordered_list_by_length
  return ordered_list_by_length
end


# Quelle est la position dans l'array de la personne @epenser ?
def position_finder(journalists)
  puts
  position = journalists.index("@epenser") #fonction ruby pour connaitre la position d'une valeur dans une array
  puts "@epenser est en position #{position} dans l'array."
  return position
end

# Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
def distribution_maker(journalists)
  puts
  max_length_handle = journalists.sort_by(&:length)[-1].length
  1.upto(max_length_handle) do |i|
    counter = 0
    journalists.each do |j|
      if j.length == i
        counter += 1
      end
    end

    if counter != 0
      puts "Nombre de handle avec #{i - 1} caractères: #{counter}"
    end
  end
end

# Methode pour afficher le menu
def menu
	puts "Selectionnez une question :"
	puts
	puts "[1] Combien y a-t-il de handle dans cette array ?"
	puts "[2] Quel est le handle le plus court de cette liste ?"
	puts "[3] Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)"
	puts "[4] Combien commencent par une majuscule (première lettre juste après le @) ?"
	puts "[5] Trie la liste de handle par ordre alphabétique."
	puts "[6] Trie la liste de handle par taille des handle (du plus court au plus long)"
	puts "[7] Quelle est la position dans l'array de la personne @epenser ?"
	puts "[8] Sors-moi une répartition des handle par longueur."
  puts
	puts "[autre touche] Quitter"
  puts
	print "Choisissez un numéro correpondant à l'index du menu ci-dessus > "
	user_input = gets.chomp.to_i #to_i pour tranformer l'input string en integer
end

#Méthode pour revenir au menu
def back_to_menu
  puts
  puts "Appuyez sur [Entrer] pour revenir au menu."
  gets
  system "clear"
end

#Mega-méthode pour enchainer les autres méthodes
def perform
  journalists = array
  puts
	case menu #case ... else fonctionne un peu comme un if ... else
	when 1
    system "clear" #pour linux uniquement, system "cls" pour windows > clear le terminal
		array_counter(journalists)
    puts
    back_to_menu
		perform
	when 2
    system "clear"
		shortest_finder(journalists)
    puts
    back_to_menu
		perform
	when 3
    system "clear"
		fiver_counter(journalists)
    puts
    back_to_menu
		perform
  when 4
    system "clear"
		uppercase_counter(journalists)
    puts
    back_to_menu
		perform
  when 5
    system "clear"
		get_alphabetical_order(journalists)
    puts
    back_to_menu
		perform
  when 6
    system "clear"
		order_by_length(journalists)
    puts
    back_to_menu
		perform
  when 7
    system "clear"
		position_finder(journalists)
    puts
    back_to_menu
		perform
  when 8
    system "clear"
		distribution_maker(journalists)
    puts
    back_to_menu
		perform
	end
end
system "clear"
perform